import React from "react";
import axios from "axios";
import Movie from "./Movie";
import "./App.css"

// https://yts-proxy.now.sh/... the ... can be used same as original YTS API
// Nomad Coder API is from YTS but as YTS's URL cotinuously changes Nomad Coder manipulated to continuously provide json data

class App extends React.Component {
  state = {
    isLoading: true,
    movies: []
  };
  getMovies = async () => {
    // as axios.get requires time and JS renders before getting the data so use await to stop for a moment
    // and as await cannot be used only by it self, async must be used together
    // ES6 format: {data: {data: {movies}}} --> need more time to understand
    const {data: {data: {movies}}} = await axios.get("https://yts-proxy.now.sh/list_movies.json?sort_by=rating");
    const movies2 = await axios.get("");

    this.setState({ movies, isLoading: false });
  };
  componentDidMount() {
    this.getMovies();
  }
  render() {
    const { isLoading, movies } = this.state;
    return (<section className="container">
      {isLoading
      ? (
        <div className="loader">
          <span className="loader__text">Loading...</span>
        </div>
      )
      : (
        <div className="movies">
          {movies.map(movie => (
            <Movie
              key={movie.id}
              title={movie.title}
              year={movie.year}
              summary={movie.summary}
              poster={movie.medium_cover_image}
              genres={movie.genres}
            />
          ))}
        </div>
      )
      }
    </section>); 
  }
}


export default App;
