import React from "react";
import "./Detail.css"

class Detail extends React.Component {
    componentDidMount() {
        const { location, history } = this.props;
        // if user unexpectedly refreshed page without clicking the movie it redirects to home
        if(location.state === undefined) {
            history.push("/");
        }
    }
    render() {
        const { location } = this.props;
        if(location.state !== undefined) {
            console.log(location);
            return (
                <div className="movie__detail">
                    <div className="movie__detail__title">{location.state.title}</div>
                    <div className="movie__detail__subinfo">{location.state.year}</div>
                    <div className="movie__detail__subinfo">{location.state.genres}</div>
                </div>
            )
        } else {
            return null
        }
    }
}

export default Detail;
