import React from "react";
import "./Detail.css"

class Detail extends React.Component {
    componentDidMount() {
        const { location, history } = this.props;
        // if user unexpectedly refreshed page without clicking the movie it redirects to home
        if(location.state === undefined) {
            history.push("/");
        }
    }
    render() {
        const { location } = this.props;
        if(location.state !== undefined) {
            console.log(location);
            return (
                <section className="movie__detail__container">
                    <div className="movie__detail">
                        <div className="movie__detail__title">{title}</div>
                        <div className="movie__detail__subinfo">{location.state.year}</div>
                        <div className="movie__detail__subinfo">{location.state.genres.map(genre => 
                            <span className="movie__detail__genre">{genre}</span>
                        )}</div>
                        <div className="movie__detail__summary">{location.state.summary}</div>
                        <img src={location.state.poster} alt=""className="movie__detail__poster"></img>
                    </div>
                </section>
            )
        } else {
            return null
        }
    }
}

export default Detail;
