import React from "react";
import axios from "axios";

// https://yts-proxy.now.sh/... the ... can be used same as original YTS API
// Nomad Coder API is from YTS but as YTS's URL cotinuously changes Nomad Coder manipulated to continuously provide json data

class App extends React.Component {
  state = {
    isLoading: true,
    movies: []
  };
  getMovies = async () => {
    const movies = await axios.get("https://yts-proxy.now.sh/list_movies.json");
  };
  componentDidMount() {
    this.getMovies();
  }
  render() {
    const { isLoading } = this.state;
    return <div>
      {isLoading ? "Loading..." : "We are ready"}
    </div>; 
  }
}




export default App;
